import React from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import Divider from '@material-ui/core/Divider';
import { NavLink } from 'react-router-dom';
import * as Icons from '@material-ui/icons';
import {connect} from 'react-redux';
import {ShowSublist, SetState} from '../ducks/navReducer.js';
import {SwitchTabs, ResetValue} from '../ducks/templateReducer.js'

class DynamicSideBar extends React.Component
{
  openSubList(oneState,bool){
   this.props.showSublist(oneState,bool);
  }

  resetTabView = () =>{
   this.props.resetValue();
  };

  renderNavItem(node)
  {
    const MyIcon = Icons[node.icon];

    if(node.subNavItems)
    {
      let listItems = this.loopSubItems(node.subNavItems);
      const expand = this.props.state[node.label] === undefined? 
        window.location.hash.indexOf(node.url) > -1 : this.props.state[node.label];
      this.props.setState(node.label,expand);
        return(
          <div key = {node.label}>
            <ListItem button onClick={()=>{this.openSubList(node.label,expand)}}>
              <ListItemIcon>
                <MyIcon />
              </ListItemIcon>
              <ListItemText inset primary={node.label} />
              {expand?<Icons.ExpandLess/> : <Icons.ExpandMore/>}
            </ListItem>
            <Divider />
            <Collapse in={expand} timeout="auto" unmountOnExit>
            <List component="div">
             {listItems}
            </List>
            </Collapse>
          </div>
      );
    }

    else
    {
      return(
        <div key={node.label}>
          <NavLink exact = {node.url === '/'} to={node.url} activeClassName="on-click">
            <ListItem button onClick = {this.resetTabView}>
              <ListItemIcon>
                <MyIcon />
              </ListItemIcon>
              <ListItemText primary={node.label} />
            </ListItem>
          </NavLink>
          <Divider />
        </div>
      );
    }
  }

  listLoop(list) {
    let output = [];
    for(let i in list)
    {
      output.push(this.renderNavItem(list[i]));
    }
    return(
      <div>
      {output}
      </div>
    );
  }

  loopSubItems(list) {
    let listItems = []
    for(let i in list) {
        listItems.push(this.renderNavItem(list[i]));
      }
    return listItems;
  }
  render() {
    const { data } = this.props;
    return(
      <div>
        {this.listLoop(data)}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    data: state.navReducer.data,
    state: state.navReducer
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    showSublist: (oneState) => {dispatch(ShowSublist(oneState))},
    setState: (oneState,bool) => {dispatch(SetState(oneState, bool))},
    resetValue: () => {dispatch(ResetValue())}
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(DynamicSideBar);
